function get_client_socket(sn)
{
    var i = 0;
    
    for ( i = 0; i < socket_arr.length; i++)
    {
        if ( sn == socket_arr[i].sn_json)
        {
            return socket_arr[i].socket;
        }
    }

    return -1;
}

