var tcpHeadLen = 4;

var gateway_socket_arr = new Array();

function send_gw_result(sock, result) {
    var head = new Buffer([0x00, 0x00, 0x00, 0x00]);

    var len = result.length;

    head[0] = len & 0xff;
    head[1] = (len >> 8) & 0xff;

    sock.write(Buffer.concat([head, new Buffer(result)]));

}

function send_result(sock, retCode, errStr, commandId) {
    var ret = {
        ret: retCode,
        errStr: errStr,
        commandId: commandId
    };
    console.log(JSON.stringify(ret));
    //sock.write(JSON.stringify(ret));
    send_gw_result(sock, JSON.stringify(ret));
}

