var express = require('express');

function check(req, res)
{
    console.log("Enter session check");

    if (req.session.hasLogined)
    {
        console.log("session check success.");
        return 1;
    }
    else
    {
        console.log("session check failure");
        res.render('login');
        return 0;
    }
}

module.exports.check = check;
